<script>
import {gsap} from 'gsap'

export default {
  mounted: function () {
    this.startAnimations()
  },
  methods: {
    textTyping: function () {
      let string = "Bonjour je suis Réda Lamini, développeur web et d'application Fullstack";
      let str = string.split("");
      let el = document.getElementById('str');
      (function animate() {
        str.length > 0 ? el.innerHTML += str.shift() : clearTimeout(running);
        let running = setTimeout(animate, 90);
      })();
    },
    startAnimations: function () {
      let tl = new TimelineLite({onUpdate: updatePercentage})

      const scene = this.$scrollmagic.scene({
        triggerElement: '.wrapper',
        triggerHook: 0,
        duration: "360%"
      })
        .setPin(".wrapper")
        .setTween(tl)

      this.$scrollmagic.addScene(scene)

      function updatePercentage(tl) {
        tl.progress()
      }

      tl.to('.img1', 1, {width: "100%", height: "100%"}, 0)
      tl.to('.title1', 0.5, {opacity: 1}, 1)
      tl.to('.profile-picture', 0.5, {opacity: 1}, 1)
      tl.to('.title1', 0.5, {opacity: 0}, 2)
